class ContentLoader{constructor(){this.progressBar=null,this.loadedSections=new Set,this.init()}init(){this.createProgressBar(),this.observeSections(),this.handleScroll()}createProgressBar(){let e=document.createElement("div");e.className="progress-bar",e.style.cssText=`
    position: fixed;
    top: 0;
    left: 0;
    width: 0%;
    height: 3px;
    background: #007bff;
    transition: width 0.3s ease;
    z-index: 1000;
  `,document.body.prepend(e),this.progressBar=e}observeSections(){let e=new IntersectionObserver(e=>{e.forEach(e=>{e.isIntersecting&&(this.loadSection(e.target),this.updateProgress())})},{threshold:.1});document.querySelectorAll(".resume-section").forEach(t=>{e.observe(t)})}loadSection(e){this.loadedSections.has(e.id)||(e.style.opacity="0",e.style.transform="translateY(20px)",setTimeout(()=>{e.style.transition="opacity 0.5s ease, transform 0.5s ease",e.style.opacity="1",e.style.transform="translateY(0)",this.loadedSections.add(e.id)},300))}handleScroll(){window.addEventListener("scroll",()=>{let e=window.scrollY/(document.documentElement.scrollHeight-window.innerHeight)*100;this.progressBar.style.width=`${e}%`})}updateProgress(){let e=document.querySelectorAll(".resume-section").length,t=this.loadedSections.size/e*100;this.progressBar.style.width=`${t}%`}}window.addEventListener("DOMContentLoaded",()=>{new ContentLoader});